import React, { useState } from 'react';
import { createRoot } from 'react-dom/client';

const App = () => {
  const [activeTab, setActiveTab] = useState('lost'); // 'lost', 'found', 'match'
  const [loggedIn, setLoggedIn] = useState(false);
  const [user, setUser] = useState({ id: '12345', email: 'student@college.edu' }); // Placeholder user
  const [lostItems, setLostItems] = useState([]);
  const [foundItems, setFoundItems] = useState([]);
  const [isPosting, setIsPosting] = useState(false);
  const [isMatching, setIsMatching] = useState(false);
  const [lostItemForm, setLostItemForm] = useState({
    name: '',
    description: '',
    location: '',
    date: '',
    contact: '',
    image: null,
  });

  const [foundItemForm, setFoundItemForm] = useState({
    name: '',
    description: '',
    location: '',
    date: '',
    contact: '',
    image: null,
  });

  // Placeholder functions for API calls
  const fetchItems = async () => {
    // In a real app, this would fetch data from your Java backend
    console.log('Fetching items...');
    setLostItems([
      { id: 'l1', name: 'Blue Backpack', description: 'Lost near the library on Oct 25.', location: 'Library', date: 'Oct 25', image: 'https://placehold.co/100x100?text=Backpack' },
      { id: 'l2', name: 'Silver Watch', description: 'Lost near the fountain.', location: 'Fountain', date: 'Oct 24', image: 'https://placehold.co/100x100?text=Watch' },
    ]);
    setFoundItems([
      { id: 'f1', name: 'Pair of Keys', description: 'Found in the cafeteria on Oct 25.', location: 'Cafeteria', date: 'Oct 25', image: 'https://placehold.co/100x100?text=Keys' },
      { id: 'f2', name: 'Black Scarf', description: 'Found in the lecture hall.', location: 'Lecture Hall', date: 'Oct 24', image: 'https://placehold.co/100x100?text=Scarf' },
    ]);
  };

  const handlePostLostItem = async (e) => {
    e.preventDefault();
    setIsPosting(true);
    console.log('Posting lost item:', lostItemForm);
    // Add logic to send data to your backend API
    setIsPosting(false);
    setLostItemForm({
      name: '',
      description: '',
      location: '',
      date: '',
      contact: '',
      image: null,
    });
    setActiveTab('lost');
  };

  const handlePostFoundItem = async (e) => {
    e.preventDefault();
    setIsPosting(true);
    console.log('Posting found item:', foundItemForm);
    // Add logic to send data to your backend API
    setIsPosting(false);
    setFoundItemForm({
      name: '',
      description: '',
      location: '',
      date: '',
      contact: '',
      image: null,
    });
    setActiveTab('found');
  };

  const handleImageUpload = (e, form, setForm) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setForm({ ...form, image: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'lost':
        return (
          <div className="space-y-4">
            <h2 className="text-xl font-semibold">Lost Items</h2>
            <form onSubmit={handlePostLostItem} className="bg-white p-6 rounded-lg shadow-md space-y-4">
              <h3 className="text-lg font-medium">Report a Lost Item</h3>
              <div>
                <label className="block text-sm font-medium text-gray-700">Item Name</label>
                <input
                  type="text"
                  required
                  value={lostItemForm.name}
                  onChange={(e) => setLostItemForm({ ...lostItemForm, name: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                  required
                  value={lostItemForm.description}
                  onChange={(e) => setLostItemForm({ ...lostItemForm, description: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                ></textarea>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Location Lost</label>
                <input
                  type="text"
                  required
                  value={lostItemForm.location}
                  onChange={(e) => setLostItemForm({ ...lostItemForm, location: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Date Lost</label>
                <input
                  type="date"
                  required
                  value={lostItemForm.date}
                  onChange={(e) => setLostItemForm({ ...lostItemForm, date: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Contact Info</label>
                <input
                  type="text"
                  required
                  value={lostItemForm.contact}
                  onChange={(e) => setLostItemForm({ ...lostItemForm, contact: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Image</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => handleImageUpload(e, lostItemForm, setLostItemForm)}
                  className="mt-1 block w-full"
                />
              </div>
              <button
                type="submit"
                className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                disabled={isPosting}
              >
                {isPosting ? 'Posting...' : 'Post Lost Item'}
              </button>
            </form>
            {lostItems.map((item) => (
              <div key={item.id} className="bg-white p-4 rounded-lg shadow-md flex items-center space-x-4">
                <img src={item.image} alt={item.name} className="w-16 h-16 rounded-lg object-cover" />
                <div>
                  <h3 className="font-semibold">{item.name}</h3>
                  <p className="text-gray-600 text-sm">{item.description}</p>
                </div>
              </div>
            ))}
          </div>
        );
      case 'found':
        return (
          <div className="space-y-4">
            <h2 className="text-xl font-semibold">Found Items</h2>
            <form onSubmit={handlePostFoundItem} className="bg-white p-6 rounded-lg shadow-md space-y-4">
              <h3 className="text-lg font-medium">Report a Found Item</h3>
              <div>
                <label className="block text-sm font-medium text-gray-700">Item Name</label>
                <input
                  type="text"
                  required
                  value={foundItemForm.name}
                  onChange={(e) => setFoundItemForm({ ...foundItemForm, name: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                  required
                  value={foundItemForm.description}
                  onChange={(e) => setFoundItemForm({ ...foundItemForm, description: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                ></textarea>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Location Found</label>
                <input
                  type="text"
                  required
                  value={foundItemForm.location}
                  onChange={(e) => setFoundItemForm({ ...foundItemForm, location: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Date Found</label>
                <input
                  type="date"
                  required
                  value={foundItemForm.date}
                  onChange={(e) => setFoundItemForm({ ...foundItemForm, date: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Contact Info</label>
                <input
                  type="text"
                  required
                  value={foundItemForm.contact}
                  onChange={(e) => setFoundItemForm({ ...foundItemForm, contact: e.target.value })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Image</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => handleImageUpload(e, foundItemForm, setFoundItemForm)}
                  className="mt-1 block w-full"
                />
              </div>
              <button
                type="submit"
                className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                disabled={isPosting}
              >
                {isPosting ? 'Posting...' : 'Post Found Item'}
              </button>
            </form>
            {foundItems.map((item) => (
              <div key={item.id} className="bg-white p-4 rounded-lg shadow-md flex items-center space-x-4">
                <img src={item.image} alt={item.name} className="w-16 h-16 rounded-lg object-cover" />
                <div>
                  <h3 className="font-semibold">{item.name}</h3>
                  <p className="text-gray-600 text-sm">{item.description}</p>
                </div>
              </div>
            ))}
          </div>
        );
      case 'match':
        return (
          <div className="p-4 space-y-4">
            <h2 className="text-xl font-semibold">Matching Suggestions</h2>
            <div className="bg-white p-4 rounded-lg shadow-md">
              <p>This is where matching suggestions would be displayed.</p>
              <p className="text-sm text-gray-500 mt-2">
                (This feature is a future upgrade and would require backend logic to match items based on criteria like name, description, and location.)
              </p>
            </div>
          </div>
        );
      case 'contact':
        return (
          <div className="p-4 space-y-4">
            <h2 className="text-xl font-semibold">Contact System</h2>
            <div className="bg-white p-4 rounded-lg shadow-md">
              <p>This section would provide a form or contact information to reach other users.</p>
              <p className="text-sm text-gray-500 mt-2">
                (This would integrate with your backend to send an email or reveal contact info after a match is confirmed by the admin.)
              </p>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  const renderAuth = () => {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="max-w-md w-full p-8 space-y-6 bg-white rounded-xl shadow-lg">
          <h2 className="text-center text-3xl font-extrabold text-gray-900">
            Sign in to your account
          </h2>
          <form className="space-y-6" onSubmit={(e) => { e.preventDefault(); setLoggedIn(true); }}>
            <div className="rounded-md shadow-sm -space-y-px">
              <div>
                <label htmlFor="email-address" className="sr-only">
                  Email address
                </label>
                <input
                  id="email-address"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Email address"
                />
              </div>
              <div>
                <label htmlFor="password" className="sr-only">
                  Password
                </label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                  placeholder="Password"
                />
              </div>
            </div>

            <div>
              <button
                type="submit"
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Sign in
              </button>
            </div>
            <div className="text-center">
              <a href="#" onClick={() => { /* Placeholder for future signup logic */ }} className="font-medium text-indigo-600 hover:text-indigo-500">
                Don't have an account? Sign up
              </a>
            </div>
          </form>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      <div className="flex min-h-screen">
        {/* Sidebar */}
        <div className="bg-white text-gray-800 w-64 p-6 flex flex-col items-start shadow-xl">
          <h1 className="text-2xl font-bold mb-8">
            <span role="img" aria-label="college icon" className="mr-2"></span>
            College Lost & Found
          </h1>
          <nav className="flex-grow">
            <ul>
              <li>
                <button
                  onClick={() => setActiveTab('lost')}
                  className={`w-full text-left py-2 px-4 rounded-lg flex items-center space-x-2 transition-colors duration-200
                  ${activeTab === 'lost' ? 'bg-indigo-100 text-indigo-700' : 'hover:bg-gray-100'}`}
                >
                  <span className="text-xl"></span>
                  <span>Lost Items</span>
                </button>
              </li>
              <li className="mt-2">
                <button
                  onClick={() => setActiveTab('found')}
                  className={`w-full text-left py-2 px-4 rounded-lg flex items-center space-x-2 transition-colors duration-200
                  ${activeTab === 'found' ? 'bg-indigo-100 text-indigo-700' : 'hover:bg-gray-100'}`}
                >
                  <span className="text-xl"></span>
                  <span>Found Items</span>
                </button>
              </li>
              <li className="mt-2">
                <button
                  onClick={() => setActiveTab('match')}
                  className={`w-full text-left py-2 px-4 rounded-lg flex items-center space-x-2 transition-colors duration-200
                  ${activeTab === 'match' ? 'bg-indigo-100 text-indigo-700' : 'hover:bg-gray-100'}`}
                >
                  <span className="text-xl"></span>
                  <span>Matching Suggestions</span>
                </button>
              </li>
              <li className="mt-2">
                <button
                  onClick={() => setActiveTab('contact')}
                  className={`w-full text-left py-2 px-4 rounded-lg flex items-center space-x-2 transition-colors duration-200
                  ${activeTab === 'contact' ? 'bg-indigo-100 text-indigo-700' : 'hover:bg-gray-100'}`}
                >
                  <span className="text-xl"></span>
                  <span>Contact System</span>
                </button>
              </li>
            </ul>
          </nav>
          <div className="mt-8">
            <button
              onClick={() => setLoggedIn(false)}
              className="w-full text-left py-2 px-4 rounded-lg flex items-center space-x-2 text-red-500 hover:bg-red-50"
            >
              <span className="text-xl"></span>
              <span>Logout</span>
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 p-8">
          <header className="bg-white p-4 rounded-lg shadow-lg flex justify-between items-center mb-8">
            <input
              type="text"
              placeholder="Search by item name, category, or location"
              className="flex-1 py-2 px-4 rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
            />
            <div className="ml-4 flex items-center space-x-2">
              <span className="text-sm text-gray-500">Filter:</span>
              <button
                className="py-2 px-4 rounded-full text-sm font-medium bg-gray-200 hover:bg-gray-300"
                onClick={fetchItems}
              >
                All
              </button>
              <button
                className="py-2 px-4 rounded-full text-sm font-medium bg-blue-500 text-white hover:bg-blue-600"
                onClick={() => setLostItems([])}
              >
                Lost
              </button>
              <button
                className="py-2 px-4 rounded-full text-sm font-medium bg-green-500 text-white hover:bg-green-600"
                onClick={() => setFoundItems([])}
              >
                Found
              </button>
            </div>
          </header>
          {renderContent()}
        </div>
      </div>
    </div>
  );
};

const rootElement = document.getElementById('root');
if (rootElement) {
  const root = createRoot(rootElement);
  root.render(<App />);
}
